Console.WriteLine(turkey.Weight);
var steamShovel = new SteamShovel(); // Implicit Typing
Console.WriteLine(steamShovel.Weight);
Console.WriteLine(steamShovel.NotThere); // Compile-time Error

// DYNAMIC EXAMPLE
